// Asset types
export const ASSET_TYPES = ['stock', 'etf', 'bond', 'reit', 'commodity'] as const;

// Helper functions
export const getAssetById = (id: string): Asset | undefined => {
  return MOCK_ASSETS.find((a) => a.id === id);
};

export const getAssetBySymbol = (symbol: string): Asset | undefined => {
  return MOCK_ASSETS.find((a) => a.symbol.toLowerCase() === symbol.toLowerCase());
};

export const getAssetsByType = (type: Asset['type']): Asset[] => {
  return MOCK_ASSETS.filter((a) => a.type === type);
};

export const getAssetsBySector = (sector: string): Asset[] => {
  return MOCK_ASSETS.filter((a) => a.sector === sector);
};

export const searchAssets = (query: string): Asset[] => {
  const lowerQuery = query.toLowerCase();
  return MOCK_ASSETS.filter(
    (a) =>
      a.symbol.toLowerCase().includes(lowerQuery) ||
      a.name.toLowerCase().includes(lowerQuery) ||
      a.sector.toLowerCase().includes(lowerQuery)
  );
};
